import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { MENUS_OP } from "../Commons/Utilities";

import Header from '../Commons/Header';

import '../../css/Dashboard.css';

const active = '';



class Dashboard extends Component {

  
    handleMenu = (nombre) => {
        alert(nombre);
    }

    render() {

        if (this.props.option != undefined) {

            this.setState({
                item: this.props.option,
                subMenu: MENUS_OP.find((k) => {
                    return k.item == this.props.option;
                }).subItem
            })

        }

        return (
            <section>

                <Header user="true"></Header>
                <div>
                    <div className="menu">
                        {
                            MENUS_OP.map((k) => {
                                return (
                                    this.props.option != undefined && this.props.option == k.item ?
                                        < div className="nav-item active" key={Date.now()} >
                                            <a href="#" className="nav-link">{k.item}</a>
                                            <i className="fas fa-chevron-right"></i>
                                        </div>
                                        :
                                        < div className="nav-item" key={Date.now()} >
                                            <a href="#" className="nav-link">{k.item}</a>
                                            <i className="fas fa-chevron-right"></i>
                                        </div>
                                )

                            })
                        }

                    </div>
                </div>

            </section >
        )
    };
}

export default withRouter(Dashboard);
