import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';

class FormLogin extends Component {
    state = {
        usuario: '',
        clave: '',
        error: false

    }


    LogIn = () => {

        //    history.push('/dashboard');
        if (this.state.usuario != '' && this.state.clave != '') {
            axios.get(`http://localhost/InventoriesAPI/api/usuario/validar?user=${this.state.usuario}&password=${this.state.clave}`)
                .then(res => {
                    console.log(res.data);
                }).catch(error => {

                })
        }

    }

    render() {
        const { history } = this.props;
        return (
            <div className="shadow p-3  bg-white form-login">
                <form className="form-login">


                    <div>
                        <label >Usuario</label>
                        <input className="u-full-width"

                            onChange={(e) => {
                                this.setState({
                                    usuario: e.target.value
                                })

                            }}

                            type="email" placeholder="test@mailbox.com"></input>
                    </div>
                    <div>

                    </div>
                    <div>
                        <label >Contraseña</label>
                        <input className="u-full-width"

                            onChange={(e) => {
                                this.setState({
                                    clave: e.target.value
                                })
                            }}
                            type="password" ></input>
                    </div>


                    <div className="form-group text-right">

                        <button type="button" className="button-primary"
                            onClick={() => {
                                this.LogIn()
                            }}
                        >Ingresar
                        </button>
                    </div>
                    {this.state.error ?
                        <div className="mensaje-error">
                            <span>Usuario y contraseña con obligatorios.</span>
                        </div>
                        : null

                    }


                </form>
            </div>
        )
    };
}

export default withRouter(FormLogin);
