import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { MENUS_OP } from "../Commons/Utilities";

import Header from '../Commons/Header';
import Options from './Options';

import Fade from '@material-ui/core/Fade';

import '../../css/Dashboard.css';


class Dashboard extends Component {

    state = {
        item: '', checked: false
    }

    handleChange = () => {
        this.setState(state => ({ checked: !state.checked }));
    };


    render() {

        const { classes } = this.props;
        const { checked } = this.state;

        return (
            <section>

                <Header user="true"></Header>
                <div>
                    <div className="menu">
                        {
                            MENUS_OP.map((k) => {
                                return (
                                    <div>
                                        {this.props.option !== undefined

                                            && this.props.option === k.item
                                            || this.state.item === k.item ?
                                            <div>
                                                < div className="nav-item active" key={k.item} >
                                                    <a onClick={e => {

                                                    }}
                                                        className="nav-link">{k.item}</a>
                                                    <i className="fas fa-chevron-right"></i>

                                                </div>
                                                <Fade in={checked}>
                                                    <div className="nav-item-child">
                                                        <ul key={Date.now()}>
                                                            {k.subItem.map(sub => {
                                                                return (
                                                                    <Options key={sub.name} option={sub}></Options>
                                                                )
                                                            })
                                                            }
                                                        </ul>
                                                    </div>
                                                </Fade>

                                            </div>
                                            :
                                            < div className="nav-item" key={k.item} >

                                                <a onClick={e => {

                                                    this.setState({
                                                        item: k.item,
                                                        checked:true

                                                    })

                                                }}
                                                    className="nav-link">{k.item}</a>
                                                <i className="fas fa-chevron-right"></i>
                                            </div>
                                        }
                                    </div>
                                )

                            })
                        }

                    </div>
                </div>

            </section >
        )
    };
}

export default withRouter(Dashboard);
