import React, { Component } from 'react';
import axios from 'axios';

import '../../css/skeleton.css';
import '../../css/Products.css';

import Header from '../Commons/Header';
import Dashboard from "../menu/Dashboard";


import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import Paper from '@material-ui/core/Paper';

import { CustomTableRow, CustomTableCell } from '../Utilities/Utilities'



const styles = theme => ({
    root: {
        width: '100%',
        marginTop: theme.spacing.unit * 3,
        overflowX: 'auto',
    },
    table: {
        minWidth: 700,
    },
});

let id = 0;
function createData(name, calories, fat, carbs, protein) {
    id += 1;
    return { id, name, calories, fat, carbs, protein };
}

const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
];




class Products extends Component {


    obtenerDatos = () => {
        axios.get('http://localhost/InventoriesAPI/api/producto').then(response => {
            this.setState({
                productos: response.data
            })
        })
    }

    componentDidMount() {
        this.obtenerDatos();
    }


    state = {
        productos:[]
    }

    render() {

        const { classes } = this.props;

        return (
            <section>
                <Header></Header>
                <div className="do-work">
                    <section>
                        <Dashboard option="Productos"></Dashboard>
                    </section>
                    <section className="form-data">
                        <h5>Consulta de productos</h5>
                        <hr />
                        {/* aqui va otro componente */}
                        <div>
                            <Paper className={classes.root}>
                                <Table className={classes.table}>
                                    <TableHead>
                                        <CustomTableRow>
                                            <CustomTableCell>Dessert (100g serving)</CustomTableCell>
                                            <CustomTableCell align="right">Calories</CustomTableCell>
                                            <CustomTableCell align="right">Fat (g)</CustomTableCell>
                                            <CustomTableCell align="right">Carbs (g)</CustomTableCell>
                                            <CustomTableCell align="right">Protein (g)</CustomTableCell>
                                        </CustomTableRow>
                                    </TableHead>
                                    <TableBody>
                                        {rows.map(row => (
                                            <CustomTableRow key={row.id}>
                                                <TableCell component="th" scope="row">
                                                    {row.name}
                                                </TableCell>
                                                <TableCell align="right">{row.calories}</TableCell>
                                                <TableCell align="right">{row.fat}</TableCell>
                                                <TableCell align="right">{row.carbs}</TableCell>
                                                <TableCell align="right">{row.protein}</TableCell>
                                            </CustomTableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </Paper>
                        </div>
                    </section>
                </div>
            </section>

        )
    };
}



export default withStyles(styles)(Products);
