import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { MENUS_OP } from "../Commons/Utilities";

import Header from '../Commons/Header';
import Options from './Options';

import '../../css/Dashboard.css';


class Dashboard extends Component {

    state = {
        item: this.props.option
    }

    udatestate = (value) => {
        this.setState({
            item: value
        })
    }    

    render() {
        
        return (
            <section>

                <Header user="true"></Header>
                <div>
                    <div className="menu">
                        {
                            MENUS_OP.map((k) => {
                                return (
                                    <div>
                                        {this.state.item !== undefined

                                            && this.state.item === k.item
                                             ?
                                            <div>
                                                < div className="nav-item active" key={uuid()} >
                                                    <a onClick={e => {

                                                    }}
                                                        className="nav-link">{k.item}</a>
                                                    <i class="fas fa-chevron-down"></i>

                                                </div>
                                                <div className="nav-item-child">
                                                    <ul key={Date.now()}>
                                                        {k.subItem.map(sub => {
                                                            return (
                                                                <Options key={sub.name} option={sub}></Options>
                                                            )
                                                        })
                                                        }
                                                    </ul>
                                                </div>

                                            </div>
                                            :
                                            < div className="nav-item" key={k.item} >

                                                <a onClick={e => {

                                                    this.setState({
                                                        item: k.item

                                                    })

                                                }}
                                                    className="nav-link">{k.item}</a>
                                                <i className="fas fa-chevron-right"></i>
                                            </div>
                                        }
                                    </div>
                                )

                            })
                        }

                    </div>
                </div>

            </section >
        )
    };
}

export default withRouter(Dashboard);
