import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { MENUS_OP } from "../Commons/Utilities";

import Header from '../Commons/Header';
import Options from '../menu/Options';

import '../../css/Dashboard.css';


class Dashboard extends Component {


    state = {
        item: '',
        subMenu: []
    }

    handleMenu = (nombre) => {
        alert(nombre);
    }

    render() {

        return (
            <section>

                <Header user="true"></Header>
                <div>
                    <div className="menu">
                        {
                            this.props.option == undefined ?

                                MENUS_OP.map((k, i) => {
                                    return (
                                        <div className="nav-item" key={i}
                                            onClick={() => {
                                                this.handleMenu(k.item);
                                            }}
                                        >
                                            <a href="#" className="nav-link">{k.item}</a>
                                            <i className="fas fa-chevron-right"></i>
                                        </div>
                                    )
                                })

                                :

                                this.props.option.map((k) => {
                                    return (
                                        <div>
                                            <div key={k.item} className="nav-item"
                                                onClick={() => {

                                                    this.handleMenu(k.item);
                                                }}
                                            >
                                                {
                                                    this.props.active != undefined &&
                                                        this.props.active == k.item ?
                                                        <a href="#" className="nav-link active">{k.item}</a>
                                                        : <a href="#" className="nav-link">{k.item}</a>
                                                }

                                                <i className="fas fa-chevron-right"></i>
                                            </div>
                                            <div className="nav-item-child">
                                                {
                                                    k.subItem.map(i => {
                                                        return (
                                                            <div key={i.name}>
                                                                <Options option={i}></Options>
                                                            </div>
                                                        )
                                                    })}
                                            </div>
                                        </div>
                                    )
                                })
                        }

                    </div>
                </div>

            </section>
        )
    };
}

export default withRouter(Dashboard);
