import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';

import Header from '../Commons/Header';
import Options from '../menu/Options';

import '../../css/Dashboard.css';
import { MENUS_OP } from "../Commons/Utilities";

class Dashboard extends Component {

    menu_padre = React.createRef();

    renderizar_Sub_menu = (e) => {
        console.log(e);
    }


    renderizar_menu = () => {
        let menu = [];

        for (let i = 0; i < MENUS_OP.length; i++) {
            const element = MENUS_OP[i];
            menu.push(
                <div className="nav-item" position={i} onClick={this.renderizar_Sub_menu(element)}>
                    <a href="#" className="nav-link">{k.item}</a>
                    <i className="fas fa-chevron-right"></i>
                </div>
            );

        }
    }

    render() {

        return (
            <section>

                <Header user="true"></Header>
                <div>

                    <div className="menu">
                        {
                            MENUS_OP.map((k, i) => {
                                return (
                                    <div>
                                        <div key={i} className="nav-item"
                                            position={i}
                                            ref={this.menu_padre}
                                            onClick={this.renderizar_Sub_menu(1)}
                                        >
                                            {/* // <div className="nav-item active"> */}
                                            {/* <i className="fas fa-2x fa-flask"></i> */}
                                            <a href="#" className="nav-link">{k.item}</a>
                                            <i className="fas fa-chevron-right"></i>
                                        </div>
                                        {/* <div className="nav-item-child">
                                            {k.subItem.map(i => {
                                                return (
                                                    <div key={i.name}>
                                                        <Options option={i}></Options>
                                                    </div>
                                                )
                                            })}
                                        </div> */}
                                    </div>
                                )
                            })
                        }


                    </div>
                </div>

            </section>
        )
    };
}

export default withRouter(Dashboard);
